import { Component, OnInit } from "@angular/core";
import { ActivatedRoute } from "@angular/router";
import { ContentfulService } from "src/app/services/contentful.service";

@Component({
  selector: "app-home",
  templateUrl: "./home.component.html",
  styleUrls: ["./home.component.scss"],
})
export class HomeComponent implements OnInit {
  isData: boolean = false;
  data: any = {};
  imageURL: string = "";

  constructor(
    private route: ActivatedRoute,
    private contentfulService: ContentfulService
  ) {}

  ngOnInit(): void {
    this.data = {};
    this.isData = false;

    this.getData();
  }

  async getData() {
    this.data = await this.contentfulService.getHomeInfo(
      "1dzCS1bzH5OS1gDNdo09X1"
    );
    this.isDataLoaded(this.data);
    console.log("data: ", this.data);
    this.imageURL = this.data.image.fields.file.url;
  }

  isDataLoaded(data: any) {
    if (data != undefined && data != null) {
      this.isData = true;
    } else {
      this.isData = false;
    }
  }
}
<section class="" id="header">
  <div class="bg">
    <div class="bg-text">
      <div class="content">
        <h2>Playa Colorada</h2>
        <h2>Playa Colorada</h2>
      </div>
    </div>
  </div>

  <div style="height: 1000px; background-color: white">
    <div class="home-page-about">
      <h2>{{ data.about }}</h2>
      <hr />
      <p>{{ data.whatOffer }}</p>
    </div>
  </div>
</section>
